import React,{useState} from "react";
import {Form,Button} from "react-bootstrap";
import {auth} from "../Firebase/Config";
import {Link} from 'react-router-dom'
import {createUserWithEmailAndPassword,GoogleAuthProvider,signInWithPopup } from "firebase/auth";


function Register() {
    const [email,setEmail]= useState("")
    const [password,setPassword]= useState("")
    const provider = new GoogleAuthProvider();

    const handleRegister=async(e)=>{
      e.preventDefault()
      try{
       const userRegister=await createUserWithEmailAndPassword(auth, email, password);
       console.log(userRegister)
      }catch(e){console.log(e)
    }
    setEmail("");
    setPassword("");

    };
    const handlegoogle=async(e)=>{
        e.preventDefault()
        try{
        const userRegister = await signInWithPopup(auth, provider)
         console.log(userRegister)
        }catch(e){console.log(e)
      }
      setEmail("");
      setPassword("");

      }


    return ( 
        <Form>
            <h1>Register Here</h1>
            <p>If you don't have an already existing account</p>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" 
          value={email}
          onChange={(e)=>
          setEmail(e.target.value)}/>
          <Form.Text className="text-muted">
            We'll never share your email with anyone else.
          </Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" placeholder="Password" value={password} onChange={(e)=>
          setPassword(e.target.value)}/>
        </Form.Group>

        <Button variant="primary" type="submit" onClick={handleRegister} 
        style={{marginRight:"10px", fontSize:"15px", width:"75px"}}>
          Submit
        </Button>
        <Button variant="info,danger" type="submit" onClick={handlegoogle} style={{fontSize:"20px"}}>
          SignUp with Google
        </Button>
        <div style={{marginBottom: "20px",marginLeft:"30px"}}>
        <Link to="/login">SignIn if you have an account </Link>
        </div>
      </Form>
     );
}

export default Register;
